'    -- Call AI Language Service "Key Phrases"',
'    l_response := apex_web_service.make_rest_request(',
'      p_url            => l_url1,',
'      p_http_method    => ''POST'',',
'      p_body           => json_object(',
'                      ''text'' value l_text_column',
'                      ),',
'      p_credential_static_id => ''<CRED>''',
'    );',
'',
'    update CALLCENTRE set AI_CATEGORY = l_response WHERE ID = r_row.ID;',
'',
'    UPDATE CALLCENTRE c SET c.keyphrases = ',
