'        columns (',
'          text varchar2(100) path ''$.text'',',
'          score number(18,16) path ''$.score''',
'        )) jt )',
'    WHERE c.ID = r_row.ID;',
'',
'    -- Turn Key Phrase list into a Vector',
'    UPDATE CALLCENTRE c SET c.CHAT_VECTOR = TO_VECTOR(VECTOR_EMBEDDING(ALL_MINILM_L6_V2 USING c.keyphrases as data)) WHERE c.ID = r_row.ID;',
'',
'    l_response := '''';',
'',
'    -- Call AI Language Service "Sentiment"',
'    l_response := apex_web_service.make_rest_request(',
'      p_url            => l_url2,',
'      p_http_method    => ''POST'',',
'      p_body           => json_object(',
'                      ''text'' value l_text_column',
'                      ),',
'      p_credential_static_id => ''<CRED>''',
'    );',
'',
'    update CALLCENTRE set AI_SENTIMENT = l_response WHERE ID = r_row.ID;',
'',
'  end loop;',
'',
'end;'))
